              </div>
              <Button
                shape="round"
                size="large"
                style={
                  item.selected
                    ? {
                        background: COLORS.coralRed,
                        color: COLORS.white,
                        fontWeight: 600,
                        border: "none",
                        minWidth: 98,
                        boxShadow: "0 2px 10px 0 rgba(248,106,90,0.08)",
                      }
                    : {
                        background: COLORS.white,
                        color: COLORS.graphiteGrey,
                        fontWeight: 600,
                        border: `1.5px solid #ECECEC`,
                        minWidth: 98,
                      }
                }
              >
                {item.btn}
              </Button>
            </div>
          </List.Item>
        )}
        split={false}
      />
    </Card>
  );
}

